import React, { useState, useEffect } from 'react';
import { Form, FormGroup, Label, Input, Button } from 'reactstrap';
import Axios from 'axios';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from 'moment';
import setHours from "date-fns/setHours";
import setMinutes from "date-fns/setMinutes";


const ReservationForm = () => {

    const [reservationDate, setReservationDate] = useState(null);
    const [listAllReservations, setListAllReservations] = useState([]);
    const [name, setName] = useState("");
    const [surname, setSurname] = useState("");
    const [disabledTimes, setDisabledTimes] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:3001/getReservations')
            .then((response) => {
                setListAllReservations(response.data);
            })
    }, []);

    console.log('reservationDate: ', reservationDate);
    console.log('name: ', name);

    const createReservation = () => {
        console.log('name, surname, time')
        Axios.post('http://localhost:3001/createReservation', {name: name, surname: surname, time: reservationDate})
            .then((response) => {
                setListAllReservations([...listAllReservations, {name: name, surname: surname, time: reservationDate}]);
            })
    }

    const isWeekday = (date) => {
      const day = date.getDay();
      return day !== 0 && day !== 6;
    };

    const handleDateChange = (date) => {
        console.log('datePicker clicked: ', date);
        const disabledTimes = listAllReservations.filter((reservation, i) => {
            // console.log("moment(time).format('YYYY MM DD') === moment(date).format('YYYY MM DD')", moment(reservation).format('YYYY MM DD') === moment(date).format('YYYY MM DD'))
            // console.log('time: ', i, reservation);
            // console.log('date: ', i, date);
            // console.log("moment(reservation).format('YYYY MM DD')", moment(reservation).format('YYYY MM DD'));
            // console.log("moment(date).format('YYYY MM DD')", moment(date).format('YYYY MM DD'))
            const datele = moment(reservation.time).format('YYYY MM DD');
            console.log('reservation.time: ', reservation.time);
            console.log('datele: ', datele)
            return moment(reservation.time).format('YYYY MM DD') === moment(date).format('YYYY MM DD')

        }
        )
        console.log('disabledTimes', disabledTimes);
        setReservationDate(date)
    }

    console.log('listAllReservations: ', listAllReservations)

  return (
    <Form>
    <FormGroup>
    <Label for="patientName">
      Name
    </Label>
    <Input
      id="patientName"
      name="patientName"
      type="text"
      onChange={(event) => {setName(event.target.value)}}
    />
  </FormGroup>
    <FormGroup>
    <Label for="patientSurname">
      Surname
    </Label>
    <Input
      id="patientSurname"
      name="patientSurname"
      type="text"
      onChange={(event) => {setSurname(event.target.value)}}
    />
  </FormGroup>
  <FormGroup>
    <Label for="patientSurname">
      Date
    </Label>
      <DatePicker 
        selected={reservationDate} 
        onChange={(date) => handleDateChange(date)}
        showTimeSelect
        minDate={moment().toDate()}
        filterDate={isWeekday}
        excludeTimes={[
          setHours(setMinutes(new Date(), 30), 13),
          setHours(setMinutes(new Date(), 30), 14),
          setHours(setMinutes(new Date(), 0), 9),
        ]}
        dateFormat="yyyy MM dd, HH:mm"
        minTime={setHours(setMinutes(new Date(), 0), 8)}
        maxTime={setHours(setMinutes(new Date(), 0), 17)}
        calendarStartDay={1}
        timeFormat={"HH:mm"}
        onKeyDown={(e) => {
            e.preventDefault();
         }}
      />
      </FormGroup>
  <Button onClick={createReservation}>
    Submit
  </Button>
  </Form>
  )
}

export default ReservationForm
