import React, { useState, useEffect } from 'react';
import { Form, FormGroup, Label, Input, Button } from 'reactstrap';
import Axios from 'axios';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from 'moment';
import setHours from "date-fns/setHours";
import setMinutes from "date-fns/setMinutes";


const ReservationForm = () => {

    const [startDate, setStartDate] = useState(null);
    const [listAllTimes, setListAllTimes] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:3001/getTimes')
            .then((response) => {
                setListAllTimes(response.data);
            })
    }, []);

    const createReservation = () => {
        Axios.post('http://localhost:3001/createTime', {})
            .then((response) => {
                alert('reservation accepted');
            })
    }

    const isWeekday = (date) => {
      const day = date.getDay();
      return day !== 0 && day !== 6;
    };

  return (
    <Form>
    <FormGroup>
    <Label for="patientName">
      Name
    </Label>
    <Input
      id="patientName"
      name="patientName"
      type="text"
    />
  </FormGroup>
    <FormGroup>
    <Label for="patientSurname">
      Surname
    </Label>
    <Input
      id="patientSurname"
      name="patientSurname"
      type="text"
    />
  </FormGroup>
  <FormGroup>
    <Label for="patientSurname">
      Date
    </Label>
      <DatePicker 
        selected={startDate} 
        onChange={(date) => setStartDate(date)}
        showTimeSelect
        minDate={moment().toDate()}
        filterDate={isWeekday}
        excludeTimes={[
          setHours(setMinutes(new Date(), 30), 13),
          setHours(setMinutes(new Date(), 30), 14),
          setHours(setMinutes(new Date(), 0), 9),
        ]}
        dateFormat="yyyy MM dd, HH:mm"
        minTime={setHours(setMinutes(new Date(), 0), 8)}
        maxTime={setHours(setMinutes(new Date(), 0), 17)}
        calendarStartDay={1}
        timeFormat={"HH:mm"}
        onKeyDown={(e) => {
            e.preventDefault();
         }}
      />
      </FormGroup>
  <Button onClick={createReservation}>
    Submit
  </Button>
  </Form>
  )
}

export default ReservationForm
